<?php
require_once 'inc/autoload.php';
$auth = App::getAuth();
$db = App::getDatabase();
$auth->connectFromCookie($db);
App::getAuth()->restrict();

if(!empty($_POST)){
	if ($_POST['hidden_data'] != "data:image/octet-stream;base64,"){
		// Get the canvas in base64
		$file = $_POST['hidden_data'];
		$file = str_replace("data:image/octet-stream;base64,", "", $file);
		$file = base64_decode($file);
		// Apply the chosen frame
		$img = new Img();
		$frame = $_POST['radio-frame'];
		$img->applyFrame($file, $frame, "image/png", 2, $db, $_SESSION['auth']->id);
		$session = new Session();
		Session::getInstance()->setFlash('success', "Votre photo a bien été téléchargée.");
	}else{
		$session = new Session();
		Session::getInstance()->setFlash('danger', "Veuillez prendre une photo.");
	}
}
App::redirect('upload.php');